components/amp/css/base:
*,
*:after,
*:before {
    box-sizing: border-box;
}
html {
    font-size: 14px
}
html,
body {
    height: auto
}

body {
    background-color: {{body-bg}};
    font-size: 1rem;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
    font-weight: 400;
    line-height: 1.5;
    color: {{body-text}};
    padding-top: 55px;
    -webkit-font-smoothing: antialiased;
