    margin-bottom: 0
}
ul {
    margin-left: 1.15rem
}
ol {
    margin-left: 1.15rem
}
dl dt {
    font-weight: 700;
}
abbr,
acronym {
    border-bottom: 1px dotted #dfdfdf;
    color: #4f4f4f;
    cursor: help;
    font-size: 90%;
    text-transform: uppercase
}
abbr {
    text-transform: none
}
blockquote,
blockquote p {
    font-size: 22px;
    line-height: 29px
}
cite {
    color: #a5a5a5;
    display: block;
    font-size: 1rem;
    line-height: 1.5;
}
.icon {
    height: 1.14286rem;
    width: 1.14286rem;
    display: inline-block;
    vertical-align: middle
}
.icon svg {
    display: inline-block;
    fill: #4f4f4f;
    height: 100%;
    vertical-align: top;
    width: 100%
}
.is-srOnly,
.modal {
    display: none;
}

.body {
    margin-bottom: 6rem;
    margin-top: 1.5rem
}

.container {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: 0 1.5rem;
    position: relative;
}

.button {
    border-radius: 0;
    border-style: solid;
    border-width: 1px;
    cursor: pointer;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
    font-weight: 400;
    line-height: normal;
    margin: 0 0 1rem;
    position: relative;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    background-color: {{secondary-button-background}};
    border-color: {{secondary-button-background}};
    color: {{secondary-button-text}};
    font-size: 1rem;
    padding: .85714rem 2.28571rem;
    border-radius: 4px;
    outline: none;
    vertical-align: middle
}
.button:hover,
.button:focus,
.button.is-active,
.button.active {
    background-color: {{secondary-button-background}};
    border-color: {{secondary-button-background}};
    color: {{secondary-button-text}}
}
.button:active {
    background-color: {{secondary-button-background}};
    border: 1px solid {{secondary-button-background}};
    color: {{secondary-button-text}}
}
.button:focus,
.button.is-active,
.button.active {
    outline: none
}
.button--primary {
    background-color: {{primary-button-background}};
    border-color: {{primary-button-background}};
    color: {{primary-button-text}}
}
.button--primary:hover,
.button--primary:focus,
.button--primary.is-active,
.button--primary.active {
    background-color: {{primary-button-background}};
    border-color: {{primary-button-background}};
    color: {{primary-button-text}};
}
.button--primary:active {
    background-color: {{primary-button-background}};
    border: 1px solid {{primary-button-background}};
    color: {{primary-button-text}}
}
.button[disabled] {
    background-color: {{disabled-button-background}};
    border-color: {{disabled-button-background}};
    color: {{disabled-button-text}};
    cursor: default
}

.button--wide {
    display: block;
    width: 100%
}

.button--icon {
    font-size: 1rem;
    padding: .78571rem
}
.button--icon svg {
    fill: {{secondary-button-text}}
}

.icon--ratingEmpty svg {
    fill: {{star-rating-empty}};
}

.icon--ratingFull svg {
    fill: {{star-rating-full}};
}

.price--rrp,
.price--non-sale,
.price--discounted {
    text-decoration: line-through;
}

